import React, { useState, useEffect } from "react";
import Slider from "react-slick";
import { useNavigate } from "react-router-dom";
import categoryData from "../Data/categories.json";
import productData from "../Data/products.json";
import { useCart } from "../../context/CartContext.tsx";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

interface Product {
  id: number;
  name: string;
  description: string;
  image: string;
  category: string;
}

const Home: React.FC = () => {
  const [selectedCategory, setSelectedCategory] = useState<string>("");
  const navigate = useNavigate();
  const { addToCart } = useCart();

  useEffect(() => {
    const savedCategory = localStorage.getItem("selectedCategory");
    if (savedCategory) {
      setSelectedCategory(savedCategory);
    }
  }, []);

  const handleCategoryClick = (category: string) => {
    setSelectedCategory(category);
    localStorage.setItem("selectedCategory", category);
  };

  const handleProductClick = (product: Product) => {
    navigate(`/buy/${product.id}`);
  };

  const handleBuyNowClick = (product: Product, event: React.MouseEvent) => {
    event.stopPropagation();
    addToCart(product);
    navigate("/basket");
  };

  const filteredProducts = selectedCategory
    ? productData.filter(
        (product: Product) => product.category === selectedCategory
      )
    : productData;

  // Slider settings
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 dark:text-white">
      <section className="container mx-auto my-8">
        <Slider {...sliderSettings}>
          <div>
            <div
              className="bg-cover bg-center h-64"
              style={{
                backgroundImage:
                  "url('https://i.pinimg.com/200x150/a3/f0/68/a3f068888a8f8629a6e9fff53b0680f3.jpg')",
              }}
            ></div>
          </div>
          <div>
            <div
              className="bg-cover bg-center h-64"
              style={{
                backgroundImage:
                  "url('data:image/jpeg;base64,')",
              }}
            ></div>
          </div>
          <div>
            <div
              className="bg-cover bg-center h-64"
              style={{
                backgroundImage:
                  "url('data:image/jpeg;base64,')",
              }}
            ></div>
          </div>
        </Slider>

        <h2 className="text-2xl font-bold mb-4">Kategoriler</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {categoryData.map((category) => (
            <button
              key={category.name}
              onClick={() => handleCategoryClick(category.name)}
              className={`p-4 bg-white dark:bg-gray-800 rounded shadow-md ${
                selectedCategory === category.name
                  ? "border-2 border-blue-500"
                  : ""
              }`}
            >
              <img
                src={category.image}
                alt={category.name}
                className="w-full h-22 object-cover mb-2 rounded"
              />
              <h3 className="text-lg font-semibold">{category.name}</h3>
            </button>
          ))}
        </div>
      </section>

      <section className="container mx-auto my-8">
        <h2 className="text-2xl font-bold mb-4">Öne Çıkan Ürünler</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
          {filteredProducts.map((product) => (
            <div
              key={product.id}
              onClick={() => handleProductClick(product)}
              className="p-2 bg-white dark:bg-gray-800 rounded shadow-md cursor-pointer"
            >
              <img
                src={product.image}
                alt={product.name}
                className="w-full h-48 object-cover mb-2 rounded"
              />
              <h3 className="text-lg font-semibold">{product.name}</h3>
              <p className="text-gray-600 dark:text-gray-300">
                {product.description}
              </p>
              <button
                onClick={(e) => handleBuyNowClick(product, e)}
                className="mt-2 bg-blue-500 text-white py-1 px-4 rounded"
              >
                Satın Al
              </button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
